import functions_framework import json import os import uuid import urllib.request from datetime import datetime from google.cloud import firestore
# Initialize Clients try: db = firestore.Client() except Exception: db = None
def log_transaction(tenant_id, method, params, status, response): """ Log transaction into the GCP Firestore multitenant ledger. """ transaction_id = str(uuid.uuid4()) if db: doc_ref = db.collection('attestation_ledger').document(transaction_id) doc_ref.set({ "attestation_id": transaction_id, "tenant_id": tenant_id, "timestamp": datetime.utcnow().isoformat(), "method": method, "request_params": params, "status": status, "cms_response": response }) return transaction_id
from shared.trust_registry import TrustRegistry
def forward_to_cms(payload): """ Forward request to the GCP CMS Agent Cloud Function. """ cms_endpoint = os.environ.get('CMS_A2A_ENDPOINT') req = urllib.request.Request( cms_endpoint, data=json.dumps(payload).encode('utf-8'), headers={'Content-Type': 'application/json'}, method='POST' ) with urllib.request.urlopen(req) as res: return json.loads(res.read().decode('utf-8'))
def forward_to_payer(payload): """ Forward request to the GCP Payer Agent Cloud Function. """ payer_endpoint = os.environ.get('PAYER_A2A_ENDPOINT') req = urllib.request.Request( payer_endpoint, data=json.dumps(payload).encode('utf-8'), headers={'Content-Type': 'application/json'}, method='POST' ) with urllib.request.urlopen(req) as res: return json.loads(res.read().decode('utf-8'))
@functions_framework.http def clearinghouse_agent(request): """ HTTP Cloud Function for Clearinghouse Agent Proxy """ request_json = request.get_json(silent=True) if not request_json: return {"error": "Invalid Request"}, 400
method = request_json.get("method") params = request_json.get("params", {}) tenant_id = params.get("provider_id", "anonymous")
if method == "attest_healthcare_data": # Call CMS Cloud Function cms_response = forward_to_cms(request_json)
# Phase 2: Verify the Verifiable Credential returned by CMS vc = cms_response.get("result", {}).get("verifiable_credential") if vc: is_valid, msg = TrustRegistry.verify_proof(vc) print(f"Clearinghouse verification: {is_valid} - {msg}") cms_response["clearinghouse_verdict"] = msg
# Log to Firestore log_transaction(tenant_id, method, params, "Verified" if is_valid else "Untrusted", cms_response)
return json.dumps(cms_response), 200, {'Content-Type': 'application/json'}
elif method == "request_prior_auth": # Route to Payer Agent payer_response = forward_to_payer(request_json)
# Log to Firestore log_transaction(tenant_id, method, params, "Routed-To-Payer", payer_response)
return json.dumps(payer_response), 200, {'Content-Type': 'application/json'}
return json.dumps({"error": "Method Restricted"}), 403

import functions_framework
import json
import os
import urllib.request
import uuid

def request_prior_auth(provider_id, patient_id, cms_vc):
    """
    Initiate an A2A Prior Auth request to a Payer.
    """
    ch_endpoint = os.environ.get('CLEARINGHOUSE_A2A_ENDPOINT')
    payload = {
        "jsonrpc": "2.0",
        "method": "request_prior_auth",
        "params": {
            "provider_id": provider_id,
            "patient_id": patient_id,
            "verifiable_credential": cms_vc,
            "clinical_data": {
                "cpt_code": "99213",
                "diagnosis": "Z00.00"
            }
        },
        "id": str(uuid.uuid4())
    }

    req = urllib.request.Request(
        ch_endpoint,
        data=json.dumps(payload).encode('utf-8'),
        headers={'Content-Type': 'application/json'},
        method='POST'
    )
    with urllib.request.urlopen(req) as res:
        return json.loads(res.read().decode('utf-8'))

@functions_framework.http
def provider_agent(request):
    """
    Healthcare Provider Agent - Phase 3 Logic
    """
    # Simulate a trigger (e.g., file upload result)
    # 1. First get Attestation (Phase 1/2) 
    # 2. Then use result to get Prior Auth (Phase 3)

    # For this demo, we'll assume a CMS VC is already available
    mock_cms_vc = {
        "id": "urn:uuid:mock-vc-123",
        "issuer": "did:web:cms.gov:agent:a2a-v1",
        "proof": {"type": "Ed25519Signature2020", "verificationMethod": "did:web:cms.gov:agent:a2a-v1#key-1"}
    }

    auth_result = request_prior_auth("GCP-PROV-99", "PAT-555", mock_cms_vc)

    return json.dumps({
        "flow": "Prior-Auth-Pipeline",
        "payer_response": auth_result
    }), 200, {'Content-Type': 'application/json'}

import functions_framework
import json
import os
import uuid
from datetime import datetime
from google.cloud import firestore
from shared.trust_registry import TrustRegistry

try:
    db = firestore.Client()
except Exception:
    db = None

def handle_prior_auth(params):
    """
    Autonomous Prior Auth Decision Logic (GCP)
    """
    vc = params.get("verifiable_credential")
    if not vc:
        return {"status": "Denied", "reason": "Missing Verifiable Credential"}

    is_valid, msg = TrustRegistry.verify_proof(vc)
    if not is_valid:
        return {"status": "Denied", "reason": f"Verification Failed: {msg}"}

    auth_id = f"GCP-AUTH-{uuid.uuid4().hex[:8].upper()}"

    # Log to Payer Firestore collection
    if db:
        doc_ref = db.collection('payer_authorizations').document(auth_id)
        doc_ref.set({
            "auth_id": auth_id,
            "timestamp": datetime.utcnow().isoformat(),
            "provider_id": params.get("provider_id"),
            "status": "Auto-Approved",
            "vc_id": vc.get("id")
        })

    return {
        "auth_id": auth_id,
        "status": "Auto-Approved",
        "notes": "Verified via CMS Trust Hub"
    }

@functions_framework.http
def payer_agent(request):
    request_json = request.get_json(silent=True)
    if not request_json or request_json.get("jsonrpc") != "2.0":
        return {"error": "Invalid Request"}, 400

    method = request_json.get("method")
    params = request_json.get("params", {})
    request_id = request_json.get("id")

    if method == "request_prior_auth":
        result = handle_prior_auth(params)
        return json.dumps({
            "jsonrpc": "2.0",
            "result": result,
            "id": request_id
        }), 200, {'Content-Type': 'application/json'}

    return json.dumps({"error": "Method not found"}), 404

