import json
import os
import uuid
import urllib.request

def request_prior_auth(provider_id, patient_id, cms_vc):
    """
    Initiate an A2A Prior Auth request to a Payer via the Clearinghouse.
    """
    ch_endpoint = os.environ.get('CLEARINGHOUSE_A2A_ENDPOINT')
    payload = {
        "jsonrpc": "2.0",
        "method": "request_prior_auth",
        "params": {
            "provider_id": provider_id,
            "patient_id": patient_id,
            "verifiable_credential": cms_vc,
            "clinical_data": {
                "cpt_code": "99213",
                "diagnosis": "Z00.00"
            }
        },
        "id": str(uuid.uuid4())
    }

    req = urllib.request.Request(
        ch_endpoint,
        data=json.dumps(payload).encode('utf-8'),
        headers={'Content-Type': 'application/json'},
        method='POST'
    )
    with urllib.request.urlopen(req) as res:
        return json.loads(res.read().decode('utf-8'))

def lambda_handler(event, context):
    """
    Provider Agent Lambda - Phase 3
    """
    # In a real scenario, this would be triggered by an S3 upload or EHR event
    provider_id = "AWS-PROV-123"
    patient_id = "PAT-ABC"

    # Mocking a VC that would have been received from CMS in Phase 1/2
    mock_cms_vc = {
        "id": "urn:uuid:mock-vc-456",
        "issuer": "did:web:cms.gov:agent:a2a-v1",
        "proof": {"type": "Ed25519Signature2020", "verificationMethod": "did:web:cms.gov:agent:a2a-v1#key-1"}
    }

    auth_result = request_prior_auth(provider_id, patient_id, mock_cms_vc)

    return {
        "statusCode": 200,
        "body": json.dumps({
            "flow": "AWS-Prior-Auth-Pipeline",
            "result": auth_result
        })
    }

import json
import os
import boto3
import uuid
from datetime import datetime
from trust_registry import TrustRegistry

def get_table():
    dynamodb = boto3.resource('dynamodb')
    return dynamodb.Table(os.environ.get('AUTH_TABLE'))

def handle_prior_auth(params):
    """
    Method: request_prior_auth
    Logic: Autonomously approves if a valid CMS-issued VC is presented.
    """
    vc = params.get("verifiable_credential")
    if not vc:
        return {"status": "Denied", "reason": "No Verifiable Credential presented"}

    # Verify the CMS VC using the Trust Registry
    is_valid, msg = TrustRegistry.verify_proof(vc)
    if not is_valid:
        return {"status": "Denied", "reason": f"Untrusted Credential: {msg}"}

    # Instant Decision Logic (Simplified)
    # In production, this would check clinical criteria (CPT codes, etc.)
    prior_auth_id = f"AUTH-{uuid.uuid4().hex[:8].upper()}"

    # Save to Payer Auth Ledger
    item = {
        "auth_id": prior_auth_id,
        "timestamp": datetime.utcnow().isoformat(),
        "provider_id": params.get("provider_id"),
        "patient_id": params.get("patient_id"),
        "status": "Auto-Approved",
        "vc_ref": vc.get("id")
    }
    get_table().put_item(Item=item)

    return {
        "prior_auth_id": prior_auth_id,
        "status": "Auto-Approved",
        "valid_until": (datetime.now().replace(year=datetime.now().year + 1)).isoformat()
    }

def lambda_handler(event, context):
    try:
        body = json.loads(event.get('body', '{}'))
        method = body.get("method")
        params = body.get("params", {})
        request_id = body.get("id")

        if method == "request_prior_auth":
            result = handle_prior_auth(params)
            return {
                "statusCode": 200,
                "body": json.dumps({"jsonrpc": "2.0", "result": result, "id": request_id})
            }

        return {"statusCode": 404, "body": json.dumps({"error": "Method not found"})}

    except Exception as e:
        return {"statusCode": 500, "body": json.dumps({"error": str(e)})}
